  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    this.route.params.subscribe(params => {
      const id = +params['id'];
      this.article = this.articles.find(a => a.id === id) || null;
    });
  }
}

<div *ngIf="article" class="pt-20">
  <!-- Hero Image -->
  <div class="relative h-[60vh] overflow-hidden">
    <div class="absolute inset-0 bg-gradient-to-b from-transparent to-brown-900/50 z-10"></div>
    <img [src]="article.image" [alt]="article.title" class="w-full h-full object-cover">

    <!-- Title Overlay -->
    <div class="absolute bottom-0 left-0 right-0 z-20 p-8 text-white">
      <div class="container mx-auto max-w-4xl">
        <div class="text-emerald-400 font-medium mb-2">{{ article.category }}</div>
        <h1 class="text-4xl md:text-5xl font-serif font-bold mb-4">{{ article.title }}</h1>
        <div class="flex items-center gap-4 text-sm">
          <span>{{ article.date }}</span>
          <span>•</span>
          <span>{{ article.readTime }} читання</span>
        </div>
      </div>
    </div>
  </div>

  <!-- Article Content -->
  <article class="py-16 bg-white">
    <div class="container mx-auto max-w-4xl px-4">
      <!-- Intro -->
      <div class="text-xl text-brown-700 leading-relaxed mb-12 font-light border-l-4 border-emerald-600 pl-6 italic">
        {{ article.content.intro }}
      </div>

      <!-- Sections -->
      <div class="space-y-10">
        <div *ngFor="let section of article.content.sections" class="prose prose-lg max-w-none">
          <h2 class="text-3xl font-serif font-bold text-brown-900 mb-4">
            {{ section.heading }}
          </h2>
          <p class="text-lg text-brown-700 leading-relaxed">
            {{ section.text }}
          </p>
        </div>
      </div>

      <!-- CTA -->
      <div class="mt-16 p-8 bg-gradient-to-br from-emerald-50 to-brown-50 rounded-2xl text-center">
        <h3 class="text-2xl font-serif font-bold text-brown-900 mb-4">
          Є запитання?
        </h3>
        <p class="text-brown-700 mb-6">
          Запишіться на консультацію для детальної діагностики та індивідуальних рекомендацій
        </p>
        <a href="tel:+380123456789" 
           class="inline-block bg-emerald-700 hover:bg-emerald-800 text-white px-8 py-3 rounded-full font-medium transition-all duration-300">
          Записатися на прийом
        </a>
      </div>

      <!-- Back Button -->
      <div class="mt-12 text-center">
        <a routerLink="/" 
           class="inline-flex items-center text-emerald-700 hover:text-emerald-800 font-medium transition-colors">
          <svg class="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7"></path>
          </svg>
          Повернутися до статей
        </a>
      </div>
    </div>
  </article>
</div>

<!-- 404 State -->
<div *ngIf="!article" class="min-h-screen flex items-center justify-center pt-20">
  <div class="text-center">
    <h1 class="text-4xl font-serif font-bold text-brown-900 mb-4">Стаття не знайдена</h1>
    <a routerLink="/" class="text-emerald-700 hover:text-emerald-800 font-medium">
      Повернутися на головну
    </a>
  </div>
</div>
