import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { DataService, Article, GalleryImage } from '../../services/data.service';

@Component({
  selector: 'app-admin',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './admin.component.html',
  styleUrl: './admin.component.scss'
})
export class AdminComponent implements OnInit {
  activeTab: 'articles' | 'gallery' = 'articles';
  articles: Article[] = [];
  gallery: GalleryImage[] = [];

  // Article form
  showArticleForm = false;
  editingArticle: Article | null = null;
  articleForm = {
    title: '',
    excerpt: '',
    category: '',
    image: '',
    date: new Date().toLocaleDateString('uk-UA', { day: 'numeric', month: 'long', year: 'numeric' }),
    readTime: '5 хв',
    content: {
      intro: '',
      sections: [{ heading: '', text: '' }]
    }
  };

  // Gallery form
  showGalleryForm = false;
  galleryForm = {
    image: '',
    alt: ''
  };

  constructor(private dataService: DataService) {}

  ngOnInit() {
    this.loadData();
  }

  loadData() {
    this.articles = this.dataService.getArticles();
    this.gallery = this.dataService.getGallery();
  }

  // Articles
  openArticleForm(article?: Article) {
    if (article) {
      this.editingArticle = article;
      this.articleForm = { ...article };
    } else {
      this.editingArticle = null;
      this.resetArticleForm();
    }
    this.showArticleForm = true;
  }

  saveArticle() {
    if (this.editingArticle) {
      this.dataService.updateArticle(this.editingArticle.id, this.articleForm as any);
    } else {
      this.dataService.addArticle(this.articleForm as any);
    }
    this.closeArticleForm();
    this.loadData();
  }

  deleteArticle(id: number) {
    if (confirm('Видалити статтю?')) {
      this.dataService.deleteArticle(id);
      this.loadData();
    }
  }

  closeArticleForm() {
    this.showArticleForm = false;
    this.resetArticleForm();
  }

  resetArticleForm() {
    this.articleForm = {
      title: '',
      excerpt: '',
      category: '',
      image: '',
      date: new Date().toLocaleDateString('uk-UA', { day: 'numeric', month: 'long', year: 'numeric' }),
      readTime: '5 хв',
      content: {
        intro: '',
        sections: [{ heading: '', text: '' }]
      }
    };
  }

  addSection() {
    this.articleForm.content.sections.push({ heading: '', text: '' });
  }

  removeSection(index: number) {
    this.articleForm.content.sections.splice(index, 1);
  }

  // Gallery
  openGalleryForm() {
    this.showGalleryForm = true;
  }

  async onImageSelect(event: any, type: 'article' | 'gallery') {
    const file = event.target.files[0];
    if (file) {
      const base64 = await this.dataService.convertImageToBase64(file);
      if (type === 'article') {
        this.articleForm.image = base64;
      } else {
        this.galleryForm.image = base64;
      }
    }
  }

  saveGalleryImage() {
    if (this.galleryForm.image) {
      this.dataService.addGalleryImage(this.galleryForm as any);
      this.closeGalleryForm();
      this.loadData();
    }
  }

  deleteGalleryImage(id: number) {
    if (confirm('Видалити фото з галереї?')) {
      this.dataService.deleteGalleryImage(id);
      this.loadData();
    }
  }

  closeGalleryForm() {
    this.showGalleryForm = false;
    this.galleryForm = { image: '', alt: '' };
  }
}

<div class="min-h-screen bg-warm-gray-50">
  <!-- Admin Header -->
  <div class="bg-warm-gray-900 text-white py-6 shadow-lg">
    <div class="container mx-auto px-4">
      <h1 class="text-3xl font-serif font-bold">Адмін-панель</h1>
      <p class="text-warm-gray-400 mt-1">Керування контентом сайту</p>
    </div>
  </div>

  <!-- Tabs -->
  <div class="bg-white border-b border-warm-gray-200">
    <div class="container mx-auto px-4">
      <div class="flex gap-4">
        <button 
          (click)="activeTab = 'articles'"
          [class.border-bronze-500]="activeTab === 'articles'"
          [class.text-bronze-600]="activeTab === 'articles'"
          class="px-6 py-4 font-medium border-b-2 border-transparent hover:text-bronze-600 transition-colors">
          Статті ({{ articles.length }})
        </button>
        <button 
          (click)="activeTab = 'gallery'"
          [class.border-bronze-500]="activeTab === 'gallery'"
          [class.text-bronze-600]="activeTab === 'gallery'"
          class="px-6 py-4 font-medium border-b-2 border-transparent hover:text-bronze-600 transition-colors">
          Галерея ({{ gallery.length }})
        </button>
      </div>
    </div>
  </div>

  <!-- Content -->
  <div class="container mx-auto px-4 py-8">

    <!-- Articles Tab -->
    <div *ngIf="activeTab === 'articles'">
      <div class="flex justify-between items-center mb-6">
        <h2 class="text-2xl font-serif font-bold text-warm-gray-900">Статті</h2>
        <button 
          (click)="openArticleForm()"
          class="bg-bronze-500 hover:bg-bronze-600 text-white px-6 py-3 rounded-full font-medium transition-all duration-300 flex items-center gap-2">
          <svg class="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"></path>
          </svg>
          Додати статтю
        </button>
      </div>

      <!-- Articles List -->
      <div class="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div *ngFor="let article of articles" class="bg-white rounded-2xl shadow-lg overflow-hidden border border-warm-gray-200">
          <div class="aspect-[4/3] overflow-hidden">
            <img [src]="article.image" [alt]="article.title" class="w-full h-full object-cover">
          </div>
          <div class="p-4">
            <div class="text-xs text-bronze-600 font-medium mb-2">{{ article.category }}</div>
            <h3 class="font-serif font-bold text-warm-gray-900 mb-2 line-clamp-2">{{ article.title }}</h3>
            <p class="text-sm text-warm-gray-600 line-clamp-2 mb-4">{{ article.excerpt }}</p>
            <div class="flex gap-2">
              <button 
                (click)="openArticleForm(article)"
                class="flex-1 bg-warm-gray-100 hover:bg-warm-gray-200 text-warm-gray-700 px-4 py-2 rounded-lg text-sm font-medium transition-colors">
                Редагувати
              </button>
              <button 
                (click)="deleteArticle(article.id)"
                class="flex-1 bg-red-50 hover:bg-red-100 text-red-600 px-4 py-2 rounded-lg text-sm font-medium transition-colors">
                Видалити
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>

    <!-- Gallery Tab -->
    <div *ngIf="activeTab === 'gallery'">
      <div class="flex justify-between items-center mb-6">
        <h2 class="text-2xl font-serif font-bold text-warm-gray-900">Галерея</h2>
        <button 
          (click)="openGalleryForm()"
          class="bg-bronze-500 hover:bg-bronze-600 text-white px-6 py-3 rounded-full font-medium transition-all duration-300 flex items-center gap-2">
          <svg class="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"></path>
          </svg>
          Додати фото
        </button>
      </div>

      <!-- Gallery Grid -->
      <div class="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        <div *ngFor="let image of gallery" class="relative group">
          <div class="aspect-square rounded-2xl overflow-hidden border border-warm-gray-200">
            <img [src]="image.image" [alt]="image.alt" class="w-full h-full object-cover">
          </div>
          <button 
            (click)="deleteGalleryImage(image.id)"
            class="absolute top-2 right-2 bg-red-500 hover:bg-red-600 text-white p-2 rounded-full opacity-0 group-hover:opacity-100 transition-all duration-300">
            <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
            </svg>
          </button>
          <p class="text-xs text-warm-gray-600 mt-2">{{ image.alt }}</p>
        </div>
      </div>
    </div>
  </div>
</div>

<!-- Article Form Modal -->
<div *ngIf="showArticleForm" class="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4 overflow-y-auto">
  <div class="bg-white rounded-3xl shadow-2xl max-w-3xl w-full my-8">
    <div class="bg-warm-gray-900 text-white p-6 rounded-t-3xl">
      <h2 class="text-2xl font-serif font-bold">{{ editingArticle ? 'Редагувати статтю' : 'Нова стаття' }}</h2>
    </div>

    <div class="p-6 max-h-[70vh] overflow-y-auto">
      <!-- Title -->
      <div class="mb-4">
        <label class="block text-warm-gray-700 font-medium mb-2">Заголовок</label>
        <input type="text" [(ngModel)]="articleForm.title" 
               class="w-full px-4 py-3 border border-warm-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-bronze-500">
      </div>

      <!-- Category -->
      <div class="mb-4">
        <label class="block text-warm-gray-700 font-medium mb-2">Категорія</label>
        <input type="text" [(ngModel)]="articleForm.category" 
               class="w-full px-4 py-3 border border-warm-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-bronze-500">
      </div>

      <!-- Excerpt -->
      <div class="mb-4">
        <label class="block text-warm-gray-700 font-medium mb-2">Короткий опис</label>
        <textarea [(ngModel)]="articleForm.excerpt" rows="3"
                  class="w-full px-4 py-3 border border-warm-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-bronze-500"></textarea>
      </div>

      <!-- Image Upload -->
      <div class="mb-4">
        <label class="block text-warm-gray-700 font-medium mb-2">Зображення</label>
        <input type="file" accept="image/*" (change)="onImageSelect($event, 'article')"
               class="w-full px-4 py-3 border border-warm-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-bronze-500">
        <div *ngIf="articleForm.image" class="mt-2">
          <img [src]="articleForm.image" class="w-32 h-32 object-cover rounded-xl">
        </div>
      </div>

      <!-- Intro -->
      <div class="mb-4">
        <label class="block text-warm-gray-700 font-medium mb-2">Вступ</label>
        <textarea [(ngModel)]="articleForm.content.intro" rows="3"
                  class="w-full px-4 py-3 border border-warm-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-bronze-500"></textarea>
      </div>

      <!-- Sections -->
      <div class="mb-4">
        <div class="flex justify-between items-center mb-3">
          <label class="block text-warm-gray-700 font-medium">Розділи</label>
          <button (click)="addSection()" 
                  class="text-bronze-600 hover:text-bronze-700 text-sm font-medium flex items-center gap-1">
            <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 4v16m8-8H4"></path>
            </svg>
            Додати розділ
          </button>
        </div>

        <div *ngFor="let section of articleForm.content.sections; let i = index" class="mb-4 p-4 bg-warm-gray-50 rounded-xl">
          <div class="flex justify-between items-center mb-2">
            <span class="text-sm font-medium text-warm-gray-600">Розділ {{ i + 1 }}</span>
            <button *ngIf="articleForm.content.sections.length > 1" 
                    (click)="removeSection(i)"
                    class="text-red-500 hover:text-red-600">
              <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
              </svg>
            </button>
          </div>
          <input type="text" [(ngModel)]="section.heading" placeholder="Заголовок розділу"
                 class="w-full px-4 py-2 border border-warm-gray-300 rounded-lg mb-2 focus:outline-none focus:ring-2 focus:ring-bronze-500">
          <textarea [(ngModel)]="section.text" rows="3" placeholder="Текст розділу"
                    class="w-full px-4 py-2 border border-warm-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-bronze-500"></textarea>
        </div>
      </div>
    </div>

    <div class="p-6 bg-warm-gray-50 rounded-b-3xl flex gap-3">
      <button (click)="closeArticleForm()" 
              class="flex-1 bg-warm-gray-200 hover:bg-warm-gray-300 text-warm-gray-700 px-6 py-3 rounded-full font-medium transition-colors">
        Скасувати
      </button>
      <button (click)="saveArticle()" 
              class="flex-1 bg-bronze-500 hover:bg-bronze-600 text-white px-6 py-3 rounded-full font-medium transition-colors">
        {{ editingArticle ? 'Зберегти зміни' : 'Створити статтю' }}
      </button>
    </div>
  </div>
</div>

<!-- Gallery Form Modal -->
<div *ngIf="showGalleryForm" class="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
  <div class="bg-white rounded-3xl shadow-2xl max-w-lg w-full">
    <div class="bg-warm-gray-900 text-white p-6 rounded-t-3xl">
      <h2 class="text-2xl font-serif font-bold">Додати фото в галерею</h2>
    </div>

    <div class="p-6">
      <!-- Alt Text -->
      <div class="mb-4">
        <label class="block text-warm-gray-700 font-medium mb-2">Опис фото</label>
        <input type="text" [(ngModel)]="galleryForm.alt" placeholder="Наприклад: Руслана в операційній"
               class="w-full px-4 py-3 border border-warm-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-bronze-500">
      </div>

      <!-- Image Upload -->
      <div class="mb-4">
        <label class="block text-warm-gray-700 font-medium mb-2">Зображення</label>
        <input type="file" accept="image/*" (change)="onImageSelect($event, 'gallery')"
               class="w-full px-4 py-3 border border-warm-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-bronze-500">
        <div *ngIf="galleryForm.image" class="mt-4">
          <img [src]="galleryForm.image" class="w-full h-48 object-cover rounded-xl">
        </div>
      </div>
    </div>

    <div class="p-6 bg-warm-gray-50 rounded-b-3xl flex gap-3">
      <button (click)="closeGalleryForm()" 
              class="flex-1 bg-warm-gray-200 hover:bg-warm-gray-300 text-warm-gray-700 px-6 py-3 rounded-full font-medium transition-colors">
        Скасувати
      </button>
      <button (click)="saveGalleryImage()" 
              class="flex-1 bg-bronze-500 hover:bg-bronze-600 text-white px-6 py-3 rounded-full font-medium transition-colors">
        Додати фото
      </button>
    </div>
  </div>
</div>
